import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

class Menu extends Component {
  render() {
    const link = {
      width: '100px',
      padding: '12px',
      margin: '0 6px 6px',
      background: 'blue',
      textDecoration: 'none',
      color: 'white',
    }
    const active = {
      width: '100px',
      padding: '12px',
      margin: '0 6px 6px',
      background: 'white',
      textDecoration: 'none',
      color: 'red',
    }

    return (
      <div className="Menu">
      <NavLink
        to="/"
        exact
        style={link}
        activeStyle={active}
      >
        Home
      </NavLink>
      <NavLink
        to="/about"
        exact
        style={link}
        activeStyle={active}
      >
        About
      </NavLink>
      </div>
    );
  }
}

export default Menu;
