import React, { Component } from 'react';

class PhotoSet extends Component {
  render() {
    const project = this.props.project;

    return (
      <div
      className="PhotoSet"
      id={project.id}
      >
        <h2>{project.title}</h2>

        <div className='Photos'>
          {this.renderPhotos(project)}
        </div>
      </div>
    );
  }

  //////////////////////


  renderPhotos = (project) => {
    if (project.columns === 2) {
      const photos = [...project.photos];
      const divs = [];
      for (i = 0; i < photos.length; i += 2) {
        const photo1 = photos[i];
        const photo2 = photos[i+1];
        const div = (
          <div className='Photo' key={photos[i]+photos[i+1]}>
            <img src={photos[i]} />
            <img src={photos[i+1]} />
          </div>
        )
        divs.push(div)
      }
      return divs;
    }
    else {
      return (
        project.photos.map(p => {
          return (
            <div className='Photo' key={p}>
              <img src={p} />
            </div>
          )
        })
      )
    }
  }
}

export default PhotoSet;
