import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

class Link extends Component {

  constructor() {
    this.state = {
      posX: 0,
      posY: 0,
      changeX: 0,
      changeY: 0,
      elmnt: null
    }
  }

  render() {
    const link = this.props.link;

    return (
      <NavLink
        to={`/${link.page}`}
        exact
        id={link.name}
        className='Link'
      >
        {link.page}
        <img
          src={link.image}
          alt={link.page}
        />
      </NavLink>
    )
  }

  componentDidMount() {
    this.setState({
      elmnt: document.querySelector(`#${this.props.link.name}`),
    })
    this.getItemFromOffering()
  }
  ///////////////////////


}

export default Link;
